func (a *NgingOauthApp) AsRow(onlyFields ...string) param.Store {
	r := param.Store{}
	if len(onlyFields) == 0 {
		r["id"] = a.Id
		r["app_id"] = a.AppId
		r["app_secret"] = a.AppSecret
		r["site_name"] = a.SiteName
		r["site_description"] = a.SiteDescription
		r["site_domains"] = a.SiteDomains
		r["scopes"] = a.Scopes
		r["group_id"] = a.GroupId
		r["disabled"] = a.Disabled
		r["created"] = a.Created
		r["updated"] = a.Updated
		return r
	}
	for _, field := range onlyFields {
		switch field {
		case "id":
			r["id"] = a.Id
		case "app_id":
			r["app_id"] = a.AppId
		case "app_secret":
			r["app_secret"] = a.AppSecret
		case "site_name":
			r["site_name"] = a.SiteName
		case "site_description":
			r["site_description"] = a.SiteDescription
		case "site_domains":
			r["site_domains"] = a.SiteDomains
		case "scopes":
			r["scopes"] = a.Scopes
		case "group_id":
			r["group_id"] = a.GroupId
		case "disabled":
			r["disabled"] = a.Disabled
		case "created":
			r["created"] = a.Created
		case "updated":
			r["updated"] = a.Updated
		}
	}
	return r
}

